import React, { useState, useEffect } from 'react';

export default function UnitConversionForm() {

    const [inputNumericalValue, setInputNumericalValue] = useState("");
    const [inputUnitOfMeasure,  setInputUnitOfMeasure]  = useState("");
    const [studentResponse,     setStudentResponse]     = useState("");
    const [targetUnitOfMeasure, setTargetUnitOfMeasure] = useState("");
    const [resultOutput,        setResultOutput]        = useState("");

    const [measureData] = useState([
        { label: "Kelvin",       value: "kelvin" },
        { label: "Celsius",      value: "celcius" },
        { label: "Fahrenheit",   value: "fahrenheit" },
        { label: "Rankine",      value: "rankine" },
        { label: "Liters",       value: "liters" },
        { label: "Tablespoons",  value: "tablespoons" },
        { label: "Cubic Inches", value: "cubic-inches" },
        { label: "Cups",         value: "cups" },
        { label: "Cubic Feet",   value: "cubic-feet" },
        { label: "Gallons",      value: "gallons" },
    ]);

    useEffect(() => {
        const handleSubmit = (event) => {
            event.preventDefault();
            // call API
            setResultOutput("lalala");
        }
    });


    return(
        <form onSubmit={handleSubmit}>
            <div>
                <label>Input Numerical Value</label>
                <input type="text" id="inputNumericalValue" value={inputNumericalValue} onChange={e => setInputNumericalValue(e.target.value)} />
            </div>

            <div>
                <label>Input Unit of Measure</label>
                <input type="text" id="inputUnitOfMeasure" value={inputUnitOfMeasure} onChange={e => setInputUnitOfMeasure(e.target.value)} />
            </div>

            <div>
                <label>Student Response</label>
                <input type="text" id="studentResponse" value={studentResponse} onChange={e => setStudentResponse(e.target.value)} />
            </div>

            <div>
                <label>Target Unit of Measure</label>
                <input type="text" id="targetUnitOfMeasure" value={targetUnitOfMeasure} onChange={e => setTargetUnitOfMeasure(e.target.value)} />
            </div>

            <div id="output">{resultOutput}</div>

            <div><input type="submit" value="Submit"/></div>

        </form>
    );
}
