import axios from 'axios';

export default conversion = async (jsonString) => {
    const response = await fetch('/api/verify-conversion', {
        method: 'POST',
        headers: {
            'Content-type': 'application/json',
        },
        body: jsonString
    });

    const body = await response.json();
    return body;
}

const mocked_conversion = async (jsonString) => {
        const response = await axios.get('/api/verify-conversion');
        const body = await response;
        return body;
}

module.exports = { conversion, mocked_response }
